import Card, { PinkCard, TopBar, BottomBar, InfoPack, SmallButton } from "./Components.js";
import "./App.css";
import React from "react";

function DeviceProtectionScreen() {
  return (
    <>
    <TopBar/>
    <div className="background-color-verylightorange page-intro-styling">
        <div>
          <h1 className="page-title-styling" style={{ color: "darkorange" }}>
            Device Protection
          </h1>
          <p>
            {<>Your device contains your personal information such as your private photos,<br/>private messages, and social media accounts. Therefore, it is very important<br/>to keep your device safe.</>}
          </p>
        </div>
        <img
          src={"Logged-In-3--Streamline-Milano.png.png"}
          className="image-styling"
          alt="security"
        />
      </div>
      <div
              style={{
                border: "none",
                backgroundColor: "rgb(247, 234, 236)",
                justifyContent: "center",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                maxWidth: "fit-content",
                borderRadius: "17px",
                margin: "30px auto",
                height: "750px",
                padding: "20px",
              }}
            >
              <h1 style={{ fontSize: "45px", textAlign: "left", width: "100%" }}>
                To keep your account safe
              </h1>
              <InfoPack
                picture={"Vector.png"}
                title={<>Use a strong and unique password</>}
                description={
                  <>
                    Use a strong password on all your accounts (social media, email,
                    instant messaging, etc.) and do not
                    <br />
                    use the same password for multiple accounts.
                  </>
                }
              />
            <SmallButton text={"Learn how to create a strong password"}/>
              <InfoPack
                picture={"Vector.png"}
                title={<>Keep your password a secret</>}
                description={
                  <>
                    Do not share your password with others and do not enter your
                    password in front of others. If you think
                    <br />
                    your account has been infiltrated, change your password
                    immediately.
                  </>
                }
              />
              <InfoPack
                picture={"Vector.png"}
                title={<>Protect your account case</>}
                description={
                  <>
                    Log out of your account after using it on a device that is not
                    yours.
                  </>
                }
              />
              <InfoPack
                picture={"Vector.png"}
                title={<>Take advantage of security settings</>}
                description={
                  <>
                    Set up security & privacy settings in your account, such as
                    limiting those who can see your posts to
                    <br />
                    "friends only", using two-factor authentication, and setting up
                    spam protection
                  </>
                }
              />
            </div>
    </>
  );
}

export default DeviceProtectionScreen;
