function Card({ title, content, picture, link, }) {
    return (
        <div>
            {
                picture ? (
                    <img src={picture} alt={title} width="500" height="333"/>
                ) : (<div style={{ fontSize: "100px", textAlign: "center" }}>ðŸ˜º</div>)
            }
            <a style={{color: "black"}} href={link} rel="noopener noreferrer" className="card-link">
                <h3>{title}</h3>
            </a>
            <p>{content}</p>
        </div>
    );
}


function MiniCard({ title, content, link,}) {
    return (
        <div>
            <button style={{backgroundColor: "darkcyan", borderRadius: "8px", fontWeight: "lighter", color: "whitesmoke", padding: "15px 30px", border: "2px solid lightgray", border: "none"}}>
                <a href={link} style={{color: "#FFFFF0", fontWeight: "lighter"}}>{title}</a>
            </button>
        </div>
    );
}


function PinkCard({ title, content}) {
    return (
        <div style={{ border: "none", padding: "20px", margin: "10px", borderRadius: "0px 15px 15px 0px", backgroundColor: "rgb(247, 234, 236)", borderLeft: "3px solid #FF5E00", boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.3)"}}>
            <h3 style={{ color: "#FF5E00"}}>{title}</h3>
            <p style={{ fontWeight: "normal"}}>{content}</p>
        </div>
    )
}

export { MiniCard, PinkCard};
export default Card;
