function Card({ title, content, picture, link, }) {
    return (
        <div>
            {
                picture ? (
                    <img src={picture} alt={title} width="500" height="333"/>
                ) : (<div style={{ fontSize: "100px", textAlign: "center" }}>ðŸ˜º</div>)
            }
            <a style={{color: "black"}} href={link} rel="noopener noreferrer" className="card-link">
                <h3>{title}</h3>
            </a>
            <p>{content}</p>
        </div>
    );
}


function MiniCard({ title, content, link,}) {
    return (
        <div>
            <button style={{backgroundColor: "darkcyan", borderRadius: "8px", fontWeight: "lighter", color: "whitesmoke", padding: "15px 30px", border: "2px solid lightgray", border: "none"}}>
                <a href={link} style={{color: "#FFFFF0", fontWeight: "lighter"}}>{title}</a>
            </button>
        </div>
    );
}


function PinkCard({ title, content, link }) {
    return (
        <a href={link} style={{ textDecoration: "none" }} target="_blank" rel="noopener noreferrer">
            <div style={{ 
                border: "none", 
                padding: "20px", 
                margin: "10px", 
                borderRadius: "0px 15px 15px 0px", 
                backgroundColor: "rgb(247, 234, 236)", 
                borderLeft: "3px solid #FF5E00", 
                boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.3)", 
                cursor: "pointer", 
                transition: "transform 0.5s ease-in-out",
                minHeight: "150px",
            }} 
            onMouseOver={(e) => e.currentTarget.style.transform = "scale(1.05)"} 
            onMouseOut={(e) => e.currentTarget.style.transform = "scale(1)"}>
                <h3 style={{ color: "#FF5E00" }}>{title}</h3>
                <p style={{ fontWeight: "normal", color: "black" }}>{content}</p>
            </div>
        </a>
    );
}

export { MiniCard, PinkCard};
export default Card;
