import Card, { PinkCard, TopBar, BottomBar, InfoPack, SmallButton } from "./Components.js";
import "./App.css";
import React from "react";

function DeviceProtectionScreen() {
  return (
    <>
    <TopBar/>
    <div className="background-color-verylightorange page-intro-styling">
        <div>
          <h1 className="page-title-styling" style={{ color: "darkorange" }}>
            Device Protection
          </h1>
          <p>
            {<>Your device contains your personal information such as your private photos,<br/>private messages, and social media accounts. Therefore, it is very important<br/>to keep your device</>}
          </p>
        </div>
        <img
          src={"Logged-In-3--Streamline-Milano.png.png"}
          className="image-styling"
          alt="security"
        />
      </div>
      <div
        style={{
          border: "none",
          backgroundColor: "rgb(247, 234, 236)",
          justifyContent: "center",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          maxWidth: "fit-content",
          borderRadius: "17px",
          margin: "30px auto",
          height: "750px",
          padding: "20px",
        }}
      >
        <h1 style={{ fontSize: "45px", textAlign: "left", width: "100%" }}>
          To keep your device safe
        </h1>
        <InfoPack
          title={<>Limit access to your device</>}
          description={
            <>
              ● Set a strong password to protect your device and set Autolock on your phone and computer.
              <br/>
              ● Perform a factory reset on your device before you give it away or sell it.
            </>
          }
        />
        <InfoPack
          title={<>Download apps from trusted sources</>}
          description={
            <>
              ● Only download applications from trusted sources such as the App Store, Google Play Store, and official
              <br/>
              software websites.
            </>
          }
        />
        <InfoPack
          title={<>Install AntiVirus and keep your device updated</>}
          description={
            <>
              Log out of your account after using it on a device that is not
              yours.
            </>
          }
        />
        <InfoPack
          title={<>Avoid suspicious links and download requests</>}
          description={
            <>
              Set up security & privacy settings in your account, such as
              limiting those who can see your posts to
              <br />
              "friends only", using two-factor authentication, and setting up
              spam protection
            </>
          }
        />
      </div>
    </>
  );
}

export default DeviceProtectionScreen;
