import React, { useState } from 'react';
import Card, { PinkCard, MiniCard, TopBar, BottomBar } from "./Components.js";
import { useNavigate } from "react-router-dom";
import "./App.css";

function HomeScreen() {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <>
      <div className="header-container">
        {/* Update your TopBar component to include the toggleMenu function */}
        <TopBar onMenuToggle={toggleMenu} />
        
        {/* Show mobile nav based on isMenuOpen state */}
        {isMenuOpen && (
          <div className="mobile-nav">
            {/* Your mobile navigation content */}
            <ul className="mobile-nav-links">
              <li className="mobile-nav-item">
                <a href="/resources">Resources</a>
              </li>
              <li className="mobile-nav-item">
                <a href="/case-studies">Case Studies</a>
              </li>
              {/* Add other navigation items */}
            </ul>
          </div>
        )}
      </div>

      {/* Wrap all your main content in a div with a class based on menu state */}
      <div className={`main-content ${isMenuOpen ? 'menu-open' : ''}`}>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "100vh",
            textAlign: "center",
            gap: "40px",
          }}
        >
          {/* Rest of your HomeScreen content remains the same */}
          {/* ... */}
        </div>
        {/* ... other content sections ... */}
      </div>
      <BottomBar/>
    </>
  );
}

export default HomeScreen;
