import "./App.css";
import React, { useState, useEffect, Children } from "react";

function Card({ title, content, picture, backgroundColor }) {
  return (
    <div
      style={{
        width: "275px",
        border: "1px solid lightgray",
        borderRadius: "10px",
        padding: "20px",
        margin: "10px",
        boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.2)",
        cursor: "pointer",
        transition: "transform 0.2s ease-in-out",
        textAlign: "center",
        maxWidth: "500px",
        backgroundColor: "white",
        backgroundColor: backgroundColor,
      }}
      onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
      onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
    >
      {picture ? (
        <img
          src={picture}
          alt={title}
          width="500"
          height="333"
          style={{
            borderRadius: "10px",
            maxHeight: "100px",
            maxWidth: "100px",
          }}
        />
      ) : (
        <div style={{ fontSize: "100px", textAlign: "center" }}>ðŸ˜º</div>
      )}
      <h3>{title}</h3>
      <p>{content}</p>
    </div>
  );
}

function MiniCard({ title, link }) {
  return (
    <div>
      <button
        style={{
          backgroundColor: "darkcyan",
          borderRadius: "8px",
          fontWeight: "lighter",
          color: "whitesmoke",
          padding: "15px 30px",
          border: "2px solid lightgray",
          border: "none",
        }}
      >
        <a href={link} style={{ color: "#FFFFF0", fontWeight: "lighter" }}>
          {title}
        </a>
      </button>
    </div>
  );
}

function PinkCard({ title, content, link }) {
  return (
    <a
      href={link}
      style={{ textDecoration: "none" }}
      target="_blank"
      rel="noopener noreferrer"
    >
      <div
        style={{
          minWidth: "200px",
          maxHeight: "150px",
          border: "none",
          padding: "20px",
          margin: "10px",
          borderRadius: "0px 15px 15px 0px",
          backgroundColor: "rgb(247, 234, 236)",
          borderLeft: "3px solid #FF5E00",
          boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.3)",
          cursor: "pointer",
          transition: "transform 0.2s ease-in-out",
          minHeight: "150px",
        }}
        onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
        onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
      >
        <h3 style={{ color: "#FF5E00" }}>{title}</h3>
        <p style={{ fontWeight: "normal", color: "black" }}>{content}</p>
      </div>
    </a>
  );
}

function TopBar() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <>
      <div>
        <div
          className="background-color-darkcyan main-header"
          style={{
            display: "flex",
            flexDirection: "column",
            paddingLeft: "50px",
            position: "relative",
          }}
        >
          <div className="header-top-row">
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "flex-start",
                marginRight: "auto",
              }}
            >
              <h2
                className="muwatana-title-styling"
                style={{ marginTop: "5px" }}
              >
                muwatana
              </h2>
              <small style={{ marginBottom: "20px" }}>
                learn to navigate the digital world safely
              </small>
            </div>

            {/* Mobile burger menu button */}
            <div className="mobile-menu-button" onClick={toggleMenu}>
              <span>â˜°</span>
            </div>

            {/* Desktop navigation */}
            <div className="navigation-tab-links desktop-nav">
              <p>
                <a
                  href="https://en.wikipedia.org/wiki/Main_Page"
                  className="nav-link"
                  style={{ margin: "10px", fontWeight: "bold" }}
                >
                  About
                </a>
              </p>
              <p>
                <a
                  href="https://en.wikipedia.org/wiki/Main_Page"
                  className="nav-link"
                  style={{ margin: "10px", fontWeight: "bold" }}
                >
                  Resources
                </a>
              </p>
              <p>
                <a
                  href="https://en.wikipedia.org/wiki/Main_Page"
                  className="nav-link"
                  style={{
                    margin: "10px",
                    whiteSpace: "nowrap",
                    fontWeight: "bold",
                  }}
                >
                  Digital Citizenship
                  <br />
                  Principles
                </a>
              </p>
              <p>
                <a
                  href="https://en.wikipedia.org/wiki/Main_Page"
                  className="nav-link"
                >
                  <button
                    style={{
                      color: "white",
                      backgroundColor: "darkOrange",
                      borderRadius: "20px",
                      padding: "1px 20px",
                      height: "30px",
                      fontWeight: "lighter",
                      margin: "10px",
                      whiteSpace: "nowrap",
                      border: "none",
                      fontWeight: "bold",
                    }}
                  >
                    Contact ISF
                  </button>
                </a>
              </p>
              <p>
                <input
                  placeholder="search this site...      ðŸ”"
                  style={{
                    marginRight: "20px",
                    height: "40px",
                    borderRadius: "5px",
                    border: "1px solid gray",
                    padding: "5px",
                  }}
                />
              </p>
              <span
                style={{
                  margin: "20px",
                  fontWeight: "bold",
                  paddingRight: "50px",
                }}
              >
                Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©
              </span>
            </div>
          </div>

          {/* Mobile navigation menu (shows when menu is open) */}
          {menuOpen && (
            <div className="mobile-nav">
              <ul className="mobile-nav-links">
                <li className="mobile-nav-item">
                  <a
                    href="https://en.wikipedia.org/wiki/Main_Page"
                    style={{ fontWeight: "bold" }}
                  >
                    About
                  </a>
                </li>
                <li className="mobile-nav-item">
                  <a
                    href="https://en.wikipedia.org/wiki/Main_Page"
                    style={{ fontWeight: "bold" }}
                  >
                    Resources
                  </a>
                </li>
                <li className="mobile-nav-item">
                  <a
                    href="https://en.wikipedia.org/wiki/Main_Page"
                    style={{ fontWeight: "bold" }}
                  >
                    Digital Citizenship Principles
                  </a>
                </li>
                <li
                  className="mobile-nav-item"
                  style={{ padding: "10px", fontWeight: "bold" }}
                >
                  <span>Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©</span>
                </li>
                <li className="mobile-nav-item contact-item">
                  <a href="https://en.wikipedia.org/wiki/Main_Page">
                    <button
                      className="contact-button"
                      style={{ padding: "17px" }}
                    >
                      Contact ISF
                    </button>
                  </a>
                </li>
              </ul>

              {/* Mobile search bar - now placed inside and at the bottom of the mobile nav menu */}
              <div className="mobile-search-container">
                <input
                  placeholder="search this site...      ðŸ”"
                  className="mobile-search-input"
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

function BottomBar() {
  return (
    <div
      className="background-color-darkcyan"
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-start",
          textAlign: "left",
          width: "80px",
        }}
      >
        <a
          href="https://en.wikipedia.org/wiki/Main_Page"
          style={{ color: "white", textDecoration: "none" }}
        >
          <small>Contact us</small>
        </a>
        <a
          href="https://en.wikipedia.org/wiki/Main_Page"
          style={{ color: "white", textDecoration: "none" }}
        >
          <small>Help</small>
        </a>
      </div>
      <a href="https://en.wikipedia.org/wiki/Main_Page" className="nav-link">
        <button
          style={{
            color: "white",
            backgroundColor: "#FF5E00",
            borderRadius: "5px",
            height: "50px",
            fontWeight: "normal",
            margin: "10px",
            whiteSpace: "nowrap",
            border: "none",
            padding: "0px 30px",
          }}
        >
          Report incidents to ISF
        </button>
      </a>
    </div>
  );
}

const InfoPackWithButton = ({ picture, alt, title, description }) => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        gap: "10px",
        textAlign: "left",
        width: "100%",
        position: "relative",
        maxWidth: "600px",
      }}
    >
      <div
        style={{
          position: "relative",
          width: "80px",
          height: "80px",
          borderRadius: "50%",
          backgroundColor: "darkcyan",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <img
          src={picture}
          alt={alt || "Image"}
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "60%",
            height: "60%",
            objectFit: "contain",
          }}
        />
      </div>

      <div style={{ flex: 1, maxWidth: "calc(100%) - 90px" }}>
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

const InfoPack = ({ title, description }) => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        gap: "10px",
        position: "relative",
        textAlign: "left",
        width: "100%",
      }}
      className="info-pack-description"
    >
      <div>
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

function SmallButton({ text }) {
  return (
    <button
      style={{
        backgroundColor: "darkorange",
        borderRadius: "30px",
        border: "none",
        padding: "10px",
        color: "white",
      }}
    >
      {text}
    </button>
  );
}

function ExploreCard({ title, text, examples }) {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <div
      className={
        !isMobile ? "explore-card-styling" : "explore-card-styling-mobile"
      }
      onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
      onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
    >
      <d className="title">{title}</d>
      <div className="example-grid">
        {examples.map((example, index) => (
          <div key={index} className="example-box">
            <h3 className="example-title">{example}</h3>
          </div>
        ))}
      </div>
      <div className="card-text">{text}</div>
      <div className="orange-button-container">
        <a href="https://en.wikipedia.org/wiki/Main_Page" className="nav-link">
          <button className="orange-button">Explore</button>
        </a>
      </div>
    </div>
  );
}

const PhishingExamples = ({ title, children, style = {} }) => {
  // Create a container style by combining default styles with the passed style prop
  const containerStyle = {
    width: "1150px",
    height: "auto",
    borderRadius: "20px",
    boxShadow: "0px 1px 5px 2px rgba(0, 0, 0, 0.3)",
    backgroundColor: "white",
    paddingBottom: "40px",
    margin: "50px auto",
    ...style, // This will override any default styles with ones passed in
  };

  return (
    <div style={containerStyle}>
      <h1
        style={{
          fontSize: "28px",
          paddingLeft: "48px",
          paddingTop: "60px",
          color: "#008B8B",
          fontWeight: "bold",
        }}
      >
        {title}
      </h1>
      <hr
        style={{
          height: "5px",
          width: "92%",
          backgroundColor: "#40E0D0",
          border: "none",
          margin: "20px auto 30px auto",
        }}
      />
      {children}
    </div>
  );
};

function Facts({ title, content, source }) {
  return (
    <div
      style={{
        minHeight: "185px",
        margin: "10px auto",
        width: "80%",
        maxWidth: "600px",
        border: "none",
        padding: "20px",
        borderRadius: "5px",
        backgroundColor: "#b2f2e8",
        borderLeft: "10px solid rgb(32, 129, 146)",
        boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.3)",
        cursor: "pointer",
        position: "relative",
      }}
    >
      <h3 style={{ color: "rgb(32, 129, 146)" }}>{title}</h3>
      <hr
        style={{
          border: "none",
          height: "2px",
          backgroundColor: "rgb(32, 129, 146)",
          marginBottom: "15px",
          width: "99%",
          marginLeft: "0",
        }}
      />
      <p style={{ fontWeight: "normal", color: "black" }}>{content}</p>
      <p style={{ position: "absolute", right: "20px" }}>
        Source:{" "}
        <i
          href={source}
          className="text-blue-600 hover:underline"
          style={{ fontStyle: "italic", textDecorationLine: "underline" }}
        >
          Phishing for Dummies (2023)
        </i>
      </p>
    </div>
  );
}

function Image({ src }) {
  return <img src={src} alt={src} />;
}

export {
  MiniCard,
  PinkCard,
  TopBar,
  BottomBar,
  InfoPackWithButton,
  InfoPack,
  SmallButton,
  ExploreCard,
  PhishingExamples,
  Facts,
  Image,
};
export default Card;
