    fontSize: isMobile ? "15px" : "32px",
    color: "#000000",
    padding: isMobile ? "0px 20px 20px" : "0px 70px 59px",
  };

  const border = (
    <div
      style={{
        height: "2px",
        width: "100%",
        backgroundColor: "#F8C0CA",
        marginTop: isMobile ? "20px" : "59px",
