    ...style,
  };

  if (style.width === "860px" && isMobile) {
    containerStyle.width = "90%";
  }

  const wordStyle = {
    backgroundColor: "#40E0D0CC",
    color: "#005050",
    borderRadius: "18px",
    width: "350px",
    height: "83px",
    display: "flex",
    alignItems: "center",
