import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <div className="App">
      <div className="navbar">
        <div className="logo-section">
          <img src="/witnesslogo.png" className="logo" alt="Witness Logo" />
          <p className="logo-text">WITNESS</p>
        </div>
        <div className="navigation-tab-links">
          <button>Resources</button>
          <button>Case Studies</button>
          <button>Digital Citizenship</button>
        </div>
      </div>

      <div className="muwatana-title-styling">
        <h1 className="muwatana-title">المواطنة الرقمية</h1>
        <p className="muwatana-subtitle">
          استخدام التكنولوجيا والإنترنت بشكل فعّال وآمن ومسؤول
        </p>
      </div>

      <div className="cards-wrapper">
        <div className="card" style={{ flexDirection: isMobile ? "column" : "row" }}>
          <img src="/twopeoplestarringateachother.png" alt="Two people staring at each other" />
          <div className="card-text">
            <h2>عنوان البطاقة</h2>
            <p>هذا هو نص البطاقة ويمكنك تعديله كما تشاء.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
