import "./App.css";
import { useState } from 'react';

function Card({ title, content, picture, backgroundColor }) {
  return (
    <div
      style={{
        width: "275x",
        border: "1px solid lightgray",
        borderRadius: "10px",
        padding: "20px",
        margin: "10px",
        boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.2)",
        cursor: "pointer",
        transition: "transform 0.2s ease-in-out",
        textAlign: "center",
        maxWidth: "500px",
        backgroundColor: "white",
        backgroundColor: backgroundColor,
      }}
      onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
      onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
    >
      {picture ? (
        <img
          src={picture}
          alt={title}
          width="500"
          height="333"
          style={{
            borderRadius: "10px",
            maxHeight: "100px",
            maxWidth: "100px",
          }}
        />
      ) : (
        <div style={{ fontSize: "100px", textAlign: "center" }}>😺</div>
      )}
      <h3>{title}</h3>
      <p>{content}</p>
    </div>
  );
}

function MiniCard({ title, link }) {
  return (
    <div>
      <button
        style={{
          backgroundColor: "darkcyan",
          borderRadius: "8px",
          fontWeight: "lighter",
          color: "whitesmoke",
          padding: "15px 30px",
          border: "2px solid lightgray",
          border: "none",
        }}
      >
        <a href={link} style={{ color: "#FFFFF0", fontWeight: "lighter" }}>
          {title}
        </a>
      </button>
    </div>
  );
}

function PinkCard({ title, content, link }) {
  return (
    <a
      href={link}
      style={{ textDecoration: "none" }}
      target="_blank"
      rel="noopener noreferrer"
    >
      <div
        style={{
          minWidth: "200px",
          maxHeight: "150px",
          border: "none",
          padding: "20px",
          margin: "10px",
          borderRadius: "0px 15px 15px 0px",
          backgroundColor: "rgb(247, 234, 236)",
          borderLeft: "3px solid #FF5E00",
          boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.3)",
          cursor: "pointer",
          transition: "transform 0.2s ease-in-out",
          minHeight: "150px",
        }}
        onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
        onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
      >
        <h3 style={{ color: "#FF5E00" }}>{title}</h3>
        <p style={{ fontWeight: "normal", color: "black" }}>{content}</p>
      </div>
    </a>
  );
}

function TopBar() {
  return (
    <>
      <div>
        <div
          className="background-color-darkcyan"
          style={{
            display: "flex",
            justifyContent: "space-between",
            paddingLeft: "50px",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              //  paddingLeft: "5px",
            }}
          >
            <h2 className="muwatana-title-styling" style={{ marginTop: "5px" }}>
              muwatana
            </h2>
            <small style={{ marginBottom: "20px" }}>
              learn to navigate the digital world safely
            </small>
          </div>
          <div className="navigation-tab-links" style={{maxWidth: "300px",}}>
            <p>
              <a
                href="https://en.wikipedia.org/wiki/Main_Page"
                className="nav-link"
                style={{ margin: "10px" }}
              >
                About
              </a>
            </p>
            <p>
              <a
                href="https://en.wikipedia.org/wiki/Main_Page"
                className="nav-link"
                style={{ margin: "10px" }}
              >
                Resources
              </a>
            </p>
            <p>
              <a
                href="https://en.wikipedia.org/wiki/Main_Page"
                className="nav-link"
                style={{ margin: "10px", whiteSpace: "nowrap" }}
              >
                Digital Citizenship
                <br />
                Principles
              </a>
            </p>
            <p>
              <a
                href="https://en.wikipedia.org/wiki/Main_Page"
                className="nav-link"
              >
                <button
                  style={{
                    color: "white",
                    backgroundColor: "darkOrange",
                    borderRadius: "20px",
                    padding: "1px 20px",
                    height: "30px",
                    fontWeight: "lighter",
                    margin: "10px",
                    whiteSpace: "nowrap",
                    border: "none",
                  }}
                >
                  Contact ISF
                </button>
              </a>
            </p>
            <p>
              <input
                placeholder="search this site...      🔍"
                style={{
                  marginRight: "20px",
                  height: "40px",
                  borderRadius: "5px",
                  border: "1px solid gray",
                  padding: "5px",
                }}
              />
            </p>
            <span style={{ margin: "20px" }}>العربية</span>
          </div>
        </div>
      </div>
    </>
  );
}

function BottomBar() {
  return (
    <div
      className="background-color-darkcyan"
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-start",
          textAlign: "left",
          width: "80px",
        }}
      >
        <a
          href="https://en.wikipedia.org/wiki/Main_Page"
          style={{ color: "white", textDecoration: "none" }}
        >
          <small>Contact us</small>
        </a>
        <a
          href="https://en.wikipedia.org/wiki/Main_Page"
          style={{ color: "white", textDecoration: "none" }}
        >
          <small>Help</small>
        </a>
      </div>
      <a href="https://en.wikipedia.org/wiki/Main_Page" className="nav-link">
        <button
          style={{
            color: "white",
            backgroundColor: "#FF5E00",
            borderRadius: "5px",
            height: "50px",
            fontWeight: "normal",
            margin: "10px",
            whiteSpace: "nowrap",
