}

const InfoPackWithButton = ({ picture, alt, title, description }) => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        gap: "10px",
        textAlign: "left",
        width: "100%",
        position: "relative",
      }}
    >
      <div
        style={{
          position: "relative",
          width: "80px",
          height: "80px",
          borderRadius: "50%",
          backgroundColor: "darkcyan",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <img
          src={picture}
          alt={alt || "Image"}
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "60%",
            height: "60%",
            objectFit: "contain",
          }}
        />
      </div>

      <div>
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

const InfoPack = ({ title, description }) => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        gap: "10px",
        position: "relative",
        textAlign: "left",
        width: "100%",
      }}
      className="info-pack-description"
    >
      <div>
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

function SmallButton({ text }) {
  return (
    <button
      style={{
        backgroundColor: "darkorange",
        borderRadius: "30px",
        border: "none",
        padding: "10px",
        color: "white",
      }}
    >
      {text}
    </button>
  );
}

function ExploreCard({ title, text, examples }) {
  return (
    <div
      className="explore-card-styling"
      onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
      onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
    >
      <d className="title">{title}</d>
      <div className="example-grid">
        {examples.map((example, index) => (
          <div key={index} className="example-box">
            <h3 className="example-title">{example}</h3>
          </div>
        ))}
      </div>
      <div className="card-text">{text}</div>
      <div className="orange-button-container">
        <a href="https://en.wikipedia.org/wiki/Main_Page" className="nav-link">
          <button className="orange-button">Explore</button>
        </a>
      </div>
    </div>
  );
}

export {
  MiniCard,
  PinkCard,
  TopBar,
  BottomBar,
  InfoPackWithButton,
  InfoPack,
  SmallButton,
  ExploreCard,
};
